parameters.param:
$isServerBuild = ![string]::IsNullOrEmpty($env:TEAMCITY_VERSION)
$nbgvCommand = if ($isServerBuild) { "cloud --format json" } else { "get-version --format json" }
$versionOutput = $null

$processInfo = New-Object System.Diagnostics.ProcessStartInfo
$processInfo.FileName = if ($IsWindows -or $env:OS -match "Windows") { "cmd.exe" } else { "/bin/bash" }
$processInfo.Arguments = if ($IsWindows -or $env:OS -match "Windows") { 
    "/c npx nbgv $nbgvCommand" 
} else { 
    "-c `"npx nbgv $nbgvCommand`"" 
}
$processInfo.UseShellExecute = $false
$processInfo.RedirectStandardOutput = $true
$processInfo.RedirectStandardError = $true
$processInfo.CreateNoWindow = $true
$processInfo.WorkingDirectory = Get-Location

$process = New-Object System.Diagnostics.Process
$process.StartInfo = $processInfo

try {
    $process.Start() | Out-Null

    # Read streams asynchronously to avoid deadlocks
    $outputTask = $process.StandardOutput.ReadToEndAsync()
    $errorTask = $process.StandardError.ReadToEndAsync()

    $process.WaitForExit()

    $output = $outputTask.Result
    $error = $errorTask.Result

    if ($process.ExitCode -eq 0) {
        $versionOutput = $output.Trim()
        Write-Host "Successfully generated version using nbgv" -ForegroundColor Green
        Write-Host $versionOutput
    }
    else {
        Write-Error "NBGV failed with exit code $($process.ExitCode)"
        if (![string]::IsNullOrEmpty($output)) {
            Write-Error "NBGV Output: $output"
        }
        if (![string]::IsNullOrEmpty($error)) {
            Write-Error "NBGV Error: $error"
        }
        exit $process.ExitCode
    }
}
catch {
    Write-Error "Exception running nbgv: $_"
    exit 1
}
finally {
    $process.Dispose()
}

param(
    [Parameter(Mandatory=$true)]
    [string]$Version
)

Write-Host "Updating package files to version: $Version" -ForegroundColor Cyan

function Update-JsonVersion {
    param(
        [string]$FilePath,
        [string]$Version
    )

    if (-not (Test-Path $FilePath)) {
        Write-Warning "$FilePath not found, skipping..."
        return $false
    }

    Write-Host "Updating $FilePath..." -ForegroundColor Cyan

    try {
        $content = Get-Content $FilePath -Raw
        $pattern = '("version"\s*:\s*)"[^"]*"'
        $replacement = "`$1`"$Version`""

        if ($content -match $pattern) {
            $newContent = $content -replace $pattern, $replacement
            Set-Content -Path $FilePath -Value $newContent -NoNewline -Encoding UTF8
            Write-Host "âœ“ Successfully updated $FilePath" -ForegroundColor Green
            return $true
        }
        else {
            Write-Warning "Could not find version field in $FilePath"
            return $false
        }
    }
    catch {
        Write-Error "Failed to update ${FilePath}: $($_.Exception.Message)"
        return $false
    }
}

# Update both files
$packageJsonUpdated = Update-JsonVersion -FilePath "package.json" -Version $Version
$packageLockUpdated = Update-JsonVersion -FilePath "package-lock.json" -Version $Version

# Summary
Write-Host "`n========================================" -ForegroundColor Cyan
Write-Host "Version Update Summary" -ForegroundColor Cyan
Write-Host "========================================" -ForegroundColor Cyan
Write-Host "Version: $Version" -ForegroundColor Yellow
Write-Host "package.json: $(if($packageJsonUpdated){'âœ“ Updated'}else{'âœ— Failed'})" -ForegroundColor $(if($packageJsonUpdated){'Green'}else{'Red'})
Write-Host "package-lock.json: $(if($packageLockUpdated){'âœ“ Updated'}else{'âœ— Failed'})" -ForegroundColor $(if($packageLockUpdated){'Green'}else{'Red'})
Write-Host "========================================`n" -ForegroundColor Cyan

# Exit with error if any update failed
if (-not $packageJsonUpdated -or -not $packageLockUpdated) {
    Write-Error "One or more package files failed to update"
    exit 1
}

Write-Host "All package files updated successfully!" -ForegroundColor Green
exit 0

# Commit and push changes to GitHub repository
try {
    Write-Host "Getting repository information..."

    # Get repository name from git remote
    $repoName = (Split-Path -Leaf (git remote get-url origin)).Trim()
    if ($LASTEXITCODE -ne 0) {
        Write-Error "Failed to get repository name. Exit code: $LASTEXITCODE"
        exit 1
    }

    # Extract repository owner from remote URL
    $remoteUrl = git remote get-url origin
    if ($remoteUrl -match 'github\.com[:/]([^/]+)/') {
        $repoOwner = $Matches[1]
    } else {
        Write-Error "Could not determine repository owner from remote URL: $remoteUrl"
        exit 1
    }

    Write-Host "Repository: $repoOwner/$repoName"
    Write-Host "Remote URL: $remoteUrl"

    # Git add
    Write-Host "`nStaging changes..."
    git add -A
    if ($LASTEXITCODE -ne 0) {
        Write-Warning "Git add failed with exit code: $LASTEXITCODE"
    }

    # Git commit
    Write-Host "`nCommitting changes..."
    git commit --no-verify -m "chore(ci): checking in changed code from local ci"
    if ($LASTEXITCODE -ne 0) {
        Write-Warning "Git commit failed with exit code: $LASTEXITCODE (possibly nothing to commit)"
        Write-Host "Skipping push."
        exit 0
    }

    # Git push with token authentication
    Write-Host "`nPushing to GitHub..."
    $pushUrl = "https://$env:GITHUB_TOKEN@github.com/$repoOwner/$repoName"
    git push $pushUrl

    if ($LASTEXITCODE -ne 0) {
        Write-Error "Git push failed with exit code: $LASTEXITCODE"
        exit 1
    }

    Write-Host "`nSuccessfully pushed changes to GitHub" -ForegroundColor Green
    exit 0
}
catch {
    Write-Error "Failed to check in to GitHub: $($_.Exception.Message)"
    exit 1
}

# ============================== # TeamCity Build Step - npm pack # ============================== # TeamCity variables $checkoutDir = "%teamcity.build.checkoutDir%" $buildNumber = "%build.number%" $artifactsDir = Join-Path -Path $checkoutDir -ChildPath "artifacts\publish" $rootPackageJson = Join-Path -Path $checkoutDir -ChildPath "package.json" $distDir = Join-Path -Path $checkoutDir -ChildPath "dist"
# Fail fast $ErrorActionPreference = "Stop"
Write-Host "==========================================" Write-Host "ğŸ“¦ npm pack - Build $buildNumber" Write-Host "==========================================" Write-Host "Checkout Dir: $checkoutDir" Write-Host "Artifacts: $artifactsDir" Write-Host "Dist Dir: $distDir" Write-Host ""
# ============================== # Validate root package.json exists # ============================== if (-not (Test-Path $rootPackageJson)) { Write-Host "##teamcity[message text='package.json not found in root: $rootPackageJson' status='ERROR']" Write-Error "âŒ package.json not found in root: $rootPackageJson" exit 1 }
# ============================== # Ensure artifacts folder exists # ============================== Write-Host "" Write-Host "ğŸ“ Ensuring artifacts directory exists..." if (-not (Test-Path $artifactsDir)) { New-Item -ItemType Directory -Path $artifactsDir -Force | Out-Null Write-Host "âœ… Artifacts directory created" } else { Write-Host "âœ… Artifacts directory exists" }
# ============================== # Copy package.json to dist folder # ============================== Write-Host "" Write-Host "ğŸ“‹ Copying package.json to dist folder..." if (-not (Test-Path $distDir)) { New-Item -ItemType Directory -Path $distDir -Force | Out-Null Write-Host "âœ… Dist directory created" } else { Write-Host "âœ… Dist directory exists" }
$distPackageJson = Join-Path -Path $distDir -ChildPath "package.json" Copy-Item -Path $rootPackageJson -Destination $distPackageJson -Force Write-Host "âœ… package.json copied to: $distPackageJson"
# ============================== # Navigate to root directory and pack # ============================== Write-Host "" Write-Host "==> Packing npm package from $checkoutDir..." Write-Host "##teamcity[progressMessage 'Packing npm package...']"
try { # Run npm pack using npm.cmd & npm.cmd pack --silent
if ($LASTEXITCODE -ne 0) { Write-Host "##teamcity[message text='npm pack failed with exit code $LASTEXITCODE' status='ERROR']" Write-Error "âŒ npm pack failed with exit code $LASTEXITCODE" exit $LASTEXITCODE }
# Find the created .tgz file (npm pack creates it in the current directory) $tgzFile = Get-ChildItem -Path . -Filter "*.tgz" | Sort-Object LastWriteTime -Descending | Select-Object -First 1
if (-not $tgzFile) { Write-Host "##teamcity[message text='No .tgz file found after npm pack' status='ERROR']" Write-Error "âŒ No .tgz file found after npm pack" exit 1 }
Write-Host "Package created: $($tgzFile.Name)" Write-Host "##teamcity[message text='npm pack created: $($tgzFile.Name)' status='NORMAL']"
# Rename to include build number $finalPackageName = "Portfolio-$buildNumber.tgz" $dest = Join-Path -Path $artifactsDir -ChildPath $finalPackageName
# Move and rename .tgz to artifacts folder Move-Item $tgzFile.FullName $dest -Force
Write-Host "âœ… Package moved to artifacts folder: $dest" Write-Host "##teamcity[message text='Package ready: $finalPackageName' status='NORMAL']"
# Publish artifact path for TeamCity Write-Host "##teamcity[publishArtifacts '$dest']"
# Set TeamCity parameter with package path Write-Host "##teamcity[setParameter name='npm.package.path' value='$dest']" Write-Host "##teamcity[setParameter name='npm.package.name' value='$finalPackageName']"
} catch { Write-Host "##teamcity[message text='npm pack failed: $($_.Exception.Message)' status='ERROR']" Write-Error "âŒ npm pack failed: $($_.Exception.Message)" exit 1 } finally { Pop-Location }
# ============================== # Summary # ============================== Write-Host "" Write-Host "##teamcity[progressMessage 'npm pack completed successfully']" Write-Host "ğŸ‰ npm pack completed successfully!" Write-Host " Package: $finalPackageName" Write-Host " Location: $artifactsDir" Write-Host ""
